mapper.select:
select ceil(COUNT(*) / #{rowSize} )
from ${table}
where ${key} like concat('%', #{searchText}, '%')

select *
from ${table} where ${searchKey} like concat('%',#{searchText}, '%')
ORDER BY ${sortKey} limit #{startPage}, #{rowSize}

select ${suggestValue}
from ${table} where ${suggestKey} like concat('%',#{searchText}, '%')
ORDER BY ${sortKey} limit #{startPage}, #{rowSize}
